drop table ACTWEB.MOTIVO_PARADA;
drop sequence ACTWEB.MOTIVO_PARADA_ID;

CREATE TABLE ACTWEB.MOTIVO_PARADA (    
  MOT_ID_MOT         NUMBER, 
  GRU_ID_GRU         NUMBER, 
  MOT_AUTO_TRAC      NUMBER, 
  MOT_NOME           VARCHAR2(050 BYTE), 
  MOT_APLICACAO      VARCHAR2(500 BYTE), 
  MOT_ATIVO          VARCHAR2(001 BYTE));

ALTER TABLE ACTWEB.MOTIVO_PARADA ADD ( CONSTRAINT PK_MOTIVO_PARAD PRIMARY KEY (MOT_ID_MOT));

CREATE SEQUENCE ACTWEB.MOTIVO_PARADA_ID
  START WITH 1
  MAXVALUE 999999999999999999999999999
  MINVALUE 1
  NOCYCLE
  CACHE 20
  NOORDER;
  
