--drop table TRECHOS;
--drop sequence TRECHOS_ID;

CREATE TABLE TRECHOS (																				  -- Cria a tabela trechos
  TRE_ID             NUMBER             NOT NULL PRIMARY KEY,		-- Identificador do trechos
  TRE_NOME           VARCHAR2(70 BYTE)  NOT NULL,								-- Nome do trecho
  TRE_SIGLA          VARCHAR2(6 BYTE)   NOT NULL,								-- Sigla do trecho
  TRE_CADASTRO       DATE               NOT NULL,								-- Data de cadastro
  TRE_ATIVO          VARCHAR2(1 BYTE)   NOT NULL);							-- Status [ S = Ativo | N = Inativo ]
 
CREATE SEQUENCE TRECHOS_ID																			-- Cria sequence (contador) da tabela
  START WITH 1                                                                                                    
  MAXVALUE 999999999999999999999999999                                                    
  MINVALUE 1
  NOCYCLE
  CACHE 20
  NOORDER;
  


